const settings = $('Get Settings & Validate').first().json.fields || $('Get Settings & Validate').first().json;
const testMode = settings['Test Mode'] || false;
const executionId = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

// Cron now handles time window, so just pass through settings
return [{ json: { in_window: true, execution_id: executionId, test_mode: testMode, test_phone: settings['Test Phone'], admin_email: settings['Admin Alert Email'], account_phone: settings['SimpleTexting Phone'] }}];

const leads=$input.all().map(i=>i.json.fields||i.json);
const settings=$('Check 8 AM - 8 PM ET').first().json;
const testMode=settings.test_mode||false;
if(testMode){console.log('[TEST MODE] Skipping timing filter');return leads.map(lead=>({json:lead}));}
const now=Date.now();
const MIN_DELAY_MS=60*60*1000;
const eligible=[];
leads.forEach(lead=>{
if(!lead['Imported At'])return;
const importedAt=new Date(lead['Imported At']).getTime();
if(isNaN(importedAt))return;
const timeSince=now-importedAt;
if(timeSince>=MIN_DELAY_MS){eligible.push(lead);}
});
console.log(`[TIMING] Eligible: ${eligible.length}`);
return eligible.map(lead=>({json:lead}));

const leads = $('Match Templates & Build Messages').all();
const responses = $input.all();
const results = [];

for (let i = 0; i < responses.length; i++) {
  const lead = leads[i].json;
  const response = responses[i].json;

  const statusCode = response.statusCode || response.status || 0;
  const hasValidId = !!response.id;
  const isSuccess = (statusCode === 200 || statusCode === 201) || hasValidId;

  if (isSuccess) {
    console.log(`[SUCCESS] ${lead.id} | id: ${response.id}`);
    results.push({
      json: {
        lead_id: lead.id,
        id: lead.id,
        'SMS Status': 'Sent',
        'SMS Last Sent At': new Date().toISOString(),
        'SMS Sent Count': (lead['SMS Sent Count'] || 0) + 1,
        'Processing Status': 'In Sequence',
        'SMS Retry Count': 0,
        'Last Error Message': '',
        audit_data: {
          Event: 'Sent',
          Status: 'Sent',
          Phone: lead.phone_digits,
          'Lead Record ID': lead.id,
          Text: lead.message_text,
          'Campaign ID': lead.campaign,
          'Execution ID': lead.execution_id,
          'Template ID': lead.template_id,
          'Test Mode': lead.test_mode,
          SimpleTexting_ID: response.id
        }
      }
    });
  } else {
    console.log(`[FAILURE] ${lead.id} | statusCode: ${statusCode}`);
    results.push({
      json: {
        lead_id: lead.id,
        id: lead.id,
        'SMS Retry Count': (lead['SMS Retry Count'] || 0) + 1,
        'Last Error Message': `HTTP ${statusCode}: ${JSON.stringify(response)}`,
        'Processing Status': 'Ready for SMS',
        audit_data: {
          Event: 'Send Failed',
          Status: 'Error',
          Phone: lead.phone_digits,
          'Lead Record ID': lead.id,
          'Campaign ID': lead.campaign,
          'Error Details': JSON.stringify(response),
          'Execution ID': lead.execution_id,
          'Retry Attempt': lead['SMS Retry Count'] || 0,
          'Test Mode': lead.test_mode
        }
      }
    });
  }
}

return results;

const settings = $('Check 8 AM - 8 PM ET').first().json;
const testMode = settings.test_mode || false;
const maxPerExecution = testMode ? 5 : 50;

const leads = $input.all().map(i => i.json.fields || i.json);
const limited = leads.slice(0, maxPerExecution);

console.log(`[RATE LIMITER] ${leads.length} -> ${limited.length}`);

return limited.map(lead => ({ json: {...lead, execution_id: settings.execution_id, test_mode: testMode, test_phone: settings.test_phone}}));
