const record = $json.fields;
const recordId = $json.id;

// TAG COUNT
const kajabi_tags = String(record['Kajabi Tags'] || '').trim();
let tag_count = 0;
if (kajabi_tags.length > 0) {
  tag_count = kajabi_tags.split(',').map(t => t.trim()).filter(t => t.length > 0).length;
}

// RECENCY
const last_reply_at = record['Last Reply At'];
const sms_last_sent_at = record['SMS Last Sent At'];
const record_created_at = record['Record Created AT'];

let most_recent_activity = null;
const dates = [
  last_reply_at ? new Date(last_reply_at) : null,
  sms_last_sent_at ? new Date(sms_last_sent_at) : null,
  record_created_at ? new Date(record_created_at) : null
].filter(d => d && !isNaN(d.getTime()));

if (dates.length > 0) {
  most_recent_activity = new Date(Math.max(...dates.map(d => d.getTime())));
}

let days_since_activity = 999;
let recency_level = 'Low';
let recency_points = 0.5;

if (most_recent_activity) {
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  most_recent_activity.setHours(0, 0, 0, 0);
  days_since_activity = Math.floor((today - most_recent_activity) / (1000 * 60 * 60 * 24));

  if (days_since_activity <= 30) {
    recency_level = 'High';
    recency_points = 2.0;
  } else if (days_since_activity <= 90) {
    recency_level = 'Medium';
    recency_points = 1.5;
  } else {
    recency_level = 'Low';
    recency_points = 0.5;
  }
}

// TOTAL SCORE
const total_score = tag_count * recency_points;

// ENGAGEMENT LEVEL
let engagement_level = 'Red';
if (total_score > 15) {
  engagement_level = 'Green';
} else if (total_score >= 8) {
  engagement_level = 'Yellow';
}

return [{
  json: {
    record_id: recordId,
    tag_count,
    recency_level,
    recency_points,
    total_score: Math.round(total_score * 10) / 10,
    engagement_level
  }
}];
