// Smart Field Mapper v4.7 - add engagement/experience fields + boolean null fix
// CRITICAL FIX: Boolean false → null for Airtable checkbox fields
const webhookData = $input.first().json;

// Extract payload
let inputData;
if (webhookData.body && typeof webhookData.body === 'object') {
  inputData = webhookData.body;
} else if (webhookData.data && typeof webhookData.data === 'object') {
  inputData = webhookData.data;
} else {
  inputData = webhookData;
}

const fieldMappings = {
  email: ['email', 'Email', 'EMAIL', 'email_address', 'emailAddress', 'e-mail', 'e_mail', 'contact_email', 'Email_Address', 'EMAIL_ADDRESS'],
  phone: ['phone', 'Phone', 'PHONE', 'phone_number', 'phoneNumber', 'mobile', 'cellphone', 'telephone', 'phone_intl', 'mobile_intl'],
  name: ['name', 'Name', 'NAME', 'full_name', 'fullName', 'contact_name', 'display_name'],
  first_name: ['first_name', 'firstName', 'fname', 'given_name', 'forename'],
  last_name: ['last_name', 'lastName', 'lname', 'surname', 'family_name'],
  company: ['company', 'Company', 'COMPANY', 'company_name', 'companyName', 'organization', 'org', 'business'],
  title: ['title', 'Title', 'job_title', 'jobTitle', 'position', 'role'],
  source_form: ['source_form', 'form_name', 'formName', 'source', 'form_id'],
  interested_in_coaching: ['interested_in_coaching', 'coaching_interest', 'wants_coaching'],
  qualified_lead: ['qualified_lead', 'is_qualified', 'qualified', 'lead_qualified'],
  contacted: ['contacted', 'was_contacted', 'contact_attempted', 'reached_out'],
  linkedin_url: ['linkedin_url', 'linkedin', 'linkedinUrl', 'linkedin_profile'],
  request_id: ['request_id', 'requestId', 'request', 'id', 'tracking_id'],
  // New fields for ICP fallback/engagement
  touchpoint_count: ['touchpoint_count','touchpoints','engagement_count'],
  course_purchaser: ['course_purchaser','purchased_course','customer'],
  experience_years: ['experience_years','years_experience','years_in_role','yir'],
  recent_role_change: ['recent_role_change','recent_promotion','recent_change','role_change','job_change'],
  sales_focused_motivation: ['sales_focused_motivation','motivation_sales','sales_motivation']
};

const normalized = {};
const mappedFields = new Set();

function findField(aliases) {
  for (const key of Object.keys(inputData)) {
    if (aliases.some(alias => alias.toLowerCase() === key.toLowerCase())) {
      mappedFields.add(key);
      return inputData[key];
    }
  }
  return null;
}

Object.entries(fieldMappings).forEach(([normalizedName, aliases]) => {
  const value = findField(aliases);
  if (value !== null && value !== undefined && value !== '') {
    normalized[normalizedName] = value;
  }
});

// Auto-split full name if needed
if (normalized.name && !normalized.first_name) {
  const parts = normalized.name.trim().split(/\s+/);
  normalized.first_name = parts[0] || '';
  normalized.last_name = parts.slice(1).join(' ') || '';
}

// 3-Field Phone Strategy
if (normalized.phone) {
  normalized.phone_recent = normalized.phone;
  normalized.phone_original = normalized.phone;
}

// Boolean conversion for Airtable - null instead of false
['interested_in_coaching', 'qualified_lead', 'contacted', 'course_purchaser', 'recent_role_change', 'sales_focused_motivation'].forEach(field => {
  if (normalized[field] !== undefined) {
    const val = String(normalized[field]).toLowerCase();
    const isTruthy = ['true', 'yes', '1', 'on', 'y', 'checked', 'enabled'].includes(val);
    normalized[field] = isTruthy ? true : null;
  }
});

// Track unknown fields
const unknownFields = Object.keys(inputData).filter(k => !mappedFields.has(k));

// Metadata
normalized.webhook_field_count = Object.keys(inputData).length;
normalized.mapped_field_count = Object.keys(normalized).length;
normalized.unknown_field_list = unknownFields.join(', ');
normalized.field_mapping_success_rate = Math.round((Object.keys(normalized).length / Object.keys(inputData).length) * 100);
normalized.normalization_version = 'v4.7-engagement-mapping';
normalized.raw_webhook_data = JSON.stringify(webhookData);

return {
  normalized,
  original: inputData,
  unknownFields,
  mappingSuccess: Object.keys(normalized).length > 0,
  timestamp: new Date().toISOString()
};

// PDL Person Processor — map key fields to normalized + success if HTTP 200 AND person data exists
const resp = $input.first().json || {};
const smart = $node['Smart Field Mapper'].json.normalized || {};

const status = resp.statusCode ?? resp.status ?? null;
const body = resp.body ?? resp;
const data = body?.data ?? body ?? {};

// FIXED: Convert to explicit boolean
const hasPerson = status === 200 && data && Boolean(
  data.linkedin_url ||
  data.full_name ||
  data.job_title ||
  data.job_company_name
);

const first = data.first_name || smart.first_name || null;
const last = data.last_name || smart.last_name || null;
const title = data.job_title || null;
const company = data.job_company_name || null;
const linkedin = data.linkedin_url || null;
const seniority = data.job_title_seniority || null;
const industry = data.job_company_industry || null;
const isSales = data.job_title_role === 'sales' || false;

const normalized = {
  ...smart,
  first_name: first,
  last_name: last,
  title_current: title || smart.title_current || null,
  company_enriched: company || smart.company_enriched || smart.company || null,
  linkedin_url: linkedin || smart.linkedin_url || null,

  pdl_seniority: seniority,
  pdl_industry: industry,
  pdl_is_sales_role: isSales,
  pdl_job_title: title,
  pdl_company_name: company,

  pdl_person_success: hasPerson,
  pdl_status_code: status,

  enrichment_vendor: 'pdl',
  enrichment_attempted: true,
  last_enriched: new Date().toISOString(),

  pdl_cost: 0.01,
  pdl_raw_error: !hasPerson ? (body?.error || body?.message || null) : null
};

return [{ json: normalized }];

// ICP Response Processor — OpenAI primary (25/40/35), robust JSON parse, rich fallback (multi-item safe)
const items = $input.all();
const outputs = [];

function clamp(n, min, max){ const x = Number(n ?? 0); return Math.max(min, Math.min(max, isNaN(x) ? 0 : x)); }
function tier(s){ const n = Number(s||0); return n>=90?'Ultra':n>=75?'High':n>=70?'Medium':n>=50?'Low':'Archive'; }

function calcFallback(d={}){
  let reasons=[]; let domainBoost=0; let humanReview=false;
  const email = d.email || '';
  const domain = (typeof email==='string' && email.includes('@')) ? email.split('@')[1].toLowerCase() : '';
  const title = String(d.pdl_job_title || d.title_current || d.title || '').toLowerCase();
  const company = String(d.company_enriched || d.pdl_company_name || d.company || '').toLowerCase();
  const experienceYears = Number(d.experience_years || 0);
  const recentRoleChange = d.recent_role_change === true;
  const touchpoints = Number(d.touchpoint_count || 0);
  const coachingYes = d.interested_in_coaching === true;
  const webinar = String(d.source_form||'').toLowerCase().includes('webinar');
  const coursePurchaser = d.course_purchaser === true;
  const salesMotivation = d.sales_focused_motivation === true;

  const techDomains = ['salesforce.com','hubspot.com','microsoft.com','google.com','amazon.com','adobe.com','zoom.us','slack.com','atlassian.com'];
  const genericDomains = ['gmail.com','yahoo.com','hotmail.com','outlook.com'];
  const knownBrands = ['salesforce','hubspot','adobe','aws','amazon web services','microsoft','google'];

  // COMPANY (0-25)
  let company_score = 0;
  if (techDomains.some(t=>domain.includes(t))) { company_score += 15; domainBoost = 15; reasons.push('Tech domain +15'); }
  if (genericDomains.includes(domain)) { company_score -= 10; domainBoost = Math.min(domainBoost, -10); reasons.push('Generic domain -10'); }
  if (/software|saas|tech|technology|cloud|platform/i.test(company)) { company_score += 10; reasons.push('B2B tech +10'); }
  if (knownBrands.some(b=>company.includes(b))) { company_score += 10; reasons.push('Known brand +10'); }
  company_score = clamp(company_score,0,25);

  // ROLE (0-40)
  let role_score = 0;
  if (title.includes('account executive') || /\bae\b/.test(title)) { role_score += 20; if (/(senior|enterprise|strategic)/.test(title)) { role_score += 5; } reasons.push('AE +20 (+5 senior/enterprise)'); }
  else if (title.includes('account director') || title.includes('account manager')) { role_score += 18; reasons.push('Account mgmt +18'); }
  else if (title.includes('sales manager') || title.includes('team lead')) { role_score += 10; reasons.push('Sales mgr +10'); }
  else if (title.includes('sales development representative') || title.includes('business development representative') || title.includes('sdr') || title.includes('bdr') || title.includes('business development')) { role_score += 3; reasons.push('SDR/BDR +3'); }
  if (experienceYears >= 3) { role_score += 10; reasons.push('3+ yrs +10'); }
  if (recentRoleChange) { role_score += 10; reasons.push('Recent role change +10'); }
  role_score = clamp(role_score,0,40);

  // ENGAGEMENT (0-35)
  let engagement_score = 0;
  if (coachingYes) { engagement_score += 10; reasons.push('Coaching yes +10'); }
  if (touchpoints >= 3) { engagement_score += 15; reasons.push('3+ touchpoints +15'); } else if (touchpoints >= 1) { engagement_score += 8; reasons.push('Some engagement +8'); }
  if (webinar) { engagement_score += 8; reasons.push('Webinar +8'); }
  if (coursePurchaser) { engagement_score += 10; reasons.push('Course purchaser +10'); }
  if (salesMotivation) { engagement_score += 5; reasons.push('Sales motivation +5'); }
  engagement_score = clamp(engagement_score,0,35);

  const total = clamp(company_score + role_score + engagement_score,0,100);
  if ((touchpoints > 5 && total < 50) || (coachingYes && !/sales|account|revenue|bd/.test(title))) { humanReview = true; }
  return {
    icp_score: total, company_score, role_score, engagement_score, icp_tier: tier(total),
    score_reasoning: reasons.join('; '), scoring_confidence: 0.75, scoring_method_used: 'domain',
    ai_success:false, ai_error:null, outreach_potential: total>=70, human_review_needed: humanReview, domain_boost_applied: domainBoost
  };
}

// Pull latest lead state and duplicate count from earlier node
const baseLead = $node['Person Data Merger (NEW)']?.json || {};
const dupFromHandler = $node['Duplicate Handler (Dynamic)']?.json?.duplicateCount;

for (const item of items) {
  const ai = item.json || {};

  // Parse assistant content (handles ```json fences)
  let content = ai?.choices?.[0]?.message?.content || ai?.content || ai?.message?.content || '';
  if (typeof content !== 'string') content = JSON.stringify(content);
  let parsed=null, parseError=null;
  if (content?.trim()) {
    try {
      let t = content.trim()
        .replace(/^```(?:json)?\s*/i,'')
        .replace(/\s*```$/i,'')
        .replace(/^[^{]*\{/,'{')
        .replace(/\}[^}]*$/, '}');
      parsed = JSON.parse(t);
    } catch (err) { parseError = err?.message || 'JSON parse error'; }
  } else { parseError = 'Empty AI content'; }

  let resultLead;
  const hasSignal = Boolean(baseLead.company_enriched || baseLead.pdl_company_name || baseLead.title_current || baseLead.pdl_job_title || baseLead.title || baseLead.company);

  if (parsed && typeof parsed==='object' && ('total_score' in parsed) && hasSignal) {
    const company_score = clamp(parsed.company_score,0,25);
    const role_score = clamp(parsed.role_score,0,40);
    const engagement_score = clamp(parsed.engagement_score,0,35);
    let total_score = clamp(parsed.total_score,0,100);

    const fb = calcFallback(baseLead);
    if (total_score < 5 && fb.icp_score > total_score) {
      resultLead = { ...baseLead, icp_scoring_attempted:true, icp_scoring_date:new Date().toISOString(), ...fb, scoring_method_used: 'domain', ai_success:false, ai_error: 'ai_low_score_guardrail' };
    } else {
      resultLead = { ...baseLead, icp_scoring_attempted:true, icp_scoring_date:new Date().toISOString(), icp_score: total_score, company_score, role_score, engagement_score, icp_tier: tier(total_score), score_reasoning: parsed.reasoning ?? 'AI scoring', scoring_confidence: clamp(parsed.confidence,0,1) || 0.85, scoring_method_used: 'ai', ai_success:true, ai_error:null, outreach_potential: total_score>=70, human_review_needed:false, domain_boost_applied:0 };
    }
  } else {
    const fb = calcFallback(baseLead);
    resultLead = { ...baseLead, icp_scoring_attempted:true, icp_scoring_date:new Date().toISOString(), ...fb, ai_error: parseError || (hasSignal ? 'AI missing required keys' : 'Insufficient signal') };
  }

  // Merge duplicateCount from handler into normalized result
  if (dupFromHandler !== undefined) {
    resultLead.duplicateCount = dupFromHandler;
  }

  outputs.push({ json: { normalized: resultLead } });
}

return outputs;

// COMPLETE REPLACEMENT - pre-scoring duplicate check (merged source + flags)
const search = $input.first()?.json || {};

// Use merged enrichment data (fallback to SFM if needed)
const lead = $node['Person Data Merger (NEW)']?.json ||
  $node['Smart Field Mapper']?.json?.normalized ||
  {};

// Check if duplicate found
const hasDup = !!(search.id || (Array.isArray(search.records) && search.records.length > 0));
const existingRecord = hasDup ? (search.records?.[0] || search) : null;
const recordId = existingRecord?.id || null;

// Existing duplicate count and increment
const existingDupCount = existingRecord?.fields?.duplicate_count || 0;
const newDupCount = hasDup ? existingDupCount + 1 : 0;

// propagate duplicate count into normalized so downstream nodes keep it
const normalizedLead = { ...lead, duplicateCount: newDupCount };

return [{
  json: {
    // duplicate metadata
    duplicate: hasDup,
    duplicateCount: newDupCount,
    recordId,

    // propagate enrichment flags for routing
    enrichment_failed: normalizedLead.enrichment_failed === true,
    pdl_person_success: normalizedLead.pdl_person_success === true,
    hunter_person_success: normalizedLead.hunter_person_success === true,
    routing_reason: normalizedLead.routing_reason || null,

    // pass normalized view for downstream
    normalized: normalizedLead,

    // convenience fields for mappers
    email: normalizedLead.email || null,
    first_name: normalizedLead.first_name || null,
    last_name: normalizedLead.last_name || null,
    company: normalizedLead.company_enriched || normalizedLead.company || null,
    title: normalizedLead.title_current || normalizedLead.title || null,
    icp_score: normalizedLead.icp_score || 0,
    icp_tier: normalizedLead.icp_tier || 'Archive'
  }
}];

// Archive Low Scores - FIXED return shape
const leadData = $json.normalized || $json || {};

console.log(`Archiving lead ${leadData.email} - ICP Score: ${leadData.icp_score}, Tier: ${leadData.icp_tier}`);

const archivedLead = {
  ...leadData,
  archived_date: new Date().toISOString(),
  archived_reason: 'icp_score_below_threshold',
  routing: 'archive',
  processing_status: 'Archived',
  processing_completed: new Date().toISOString()
};

// n8n requires an array of items, each with { json: ... }
return [{ json: { normalized: archivedLead } }];

// Hunter Response Normalization (plan-compliant)
const items = $input.all();
for (const item of items) {
  const base = $node['Smart Field Mapper'].json.normalized || {};
  const resp = item.json || {};
  const status = resp.statusCode ?? resp.status ?? null;
  const body = resp.body ?? resp;
  const data = body?.data ?? body ?? {};

  const linkedinHandle = data?.linkedin?.handle || null;
  const title = data?.employment?.title || data?.position?.title || data?.job?.title || null;
  const companyName = data?.employment?.name || data?.company?.name || data?.organization?.name || data?.company || null;
  const first = data?.name?.givenName || data?.first_name || null;
  const last = data?.name?.familyName || data?.last_name || null;

  const normalized = {
    ...base,
    // Prefer enriched names when available
    first_name: first || base.first_name || null,
    last_name: last || base.last_name || null,
    linkedin_url: linkedinHandle ? `https://www.linkedin.com/in/${linkedinHandle}` : (base.linkedin_url || null),
    title_current: title || base.title_current || base.pdl_current_title || base.pdl_job_title || null,
    company_enriched: companyName || base.company_enriched || base.pdl_company_name || base.pdl_current_company || null,
    hunter_person_success: Boolean(linkedinHandle || title || companyName || first || last),
    enrichment_vendor: 'hunter',
    enrichment_attempted: true,
    hunter_status_code: status ?? null,
    hunter_cost: 0.049,
    last_enriched: new Date().toISOString(),
    enrichment_failed: false
  };

  item.json = normalized;
}
return items;

// Person Data Merger (PDL first, then Hunter; decide final failure)
const items = $input.all();

for (const item of items) {
  const d = item.json || {};

  const pdlSuccess = d.pdl_person_success === true;
  const hunterSuccess = d.hunter_person_success === true;

  if (pdlSuccess) {
    d.enrichment_vendor = 'pdl';
    d.enrichment_method_primary = 'pdl';
    d.enrichment_failed = false;
  } else if (hunterSuccess) {
    d.enrichment_vendor = 'hunter';
    d.enrichment_method_primary = 'hunter';
    d.enrichment_failed = false;
  } else {
    d.enrichment_failed = true;
    d.routing_decision = 'human_review';
    d.routing_reason = 'person_enrichment_failed_both_providers';
  }

  if (d.linkedin_url && !String(d.linkedin_url).startsWith('http')) {
    d.linkedin_url = `https://www.linkedin.com/in/${String(d.linkedin_url).replace(/^\/+/, '')}`;
  }

  item.json = d;
}

return items;

