	if (!url || typeof url !== 'string') return '';
	return url
		.trim()
		.replace(/^https?:\/\//i, "") // remove protocol
		.replace(/^www\./i, "")       // remove www
		.replace(/\/$/, "")           // remove trailing slash
		.toLowerCase();
}

