// Proper way to check env vars in n8n
const requiredVars = [
  'AIRTABLE_BASE_ID', 'TEST_MODE', 'DAILY_COST_LIMIT',
  'APOLLO_ORG_COST', 'APOLLO_PERSON_COST', 'TWILIO_COST',
  'SMS_COST', 'CLAUDE_API_COST', 'MAX_RETRIES',
  'CACHE_EXPIRY_DAYS', 'BATCH_SIZE', 'RETRY_DELAY_MS',
  'TEN_DLC_REGISTERED', 'SMS_MONTHLY_LIMIT', 'KAJABI_API_KEY',
  'SIMPLETEXTING_API_KEY', 'APOLLO_API_KEY'
];

const results = {};
const missing = [];
const configured = [];

// Check each variable
for (const varName of requiredVars) {
  try {
    const value = $env[varName];
    if (value !== undefined && value !== null && value !== '') {
      configured.push(varName);
      results[varName] = '✓ Set';
    } else {
      missing.push(varName);
      results[varName] = '✗ Missing';
    }
  } catch (e) {
    missing.push(varName);
    results[varName] = '✗ Error accessing';
  }
}

// Return comprehensive results
return {
  json: {
    timestamp: new Date().toISOString(),
    total_required: requiredVars.length,
    configured_count: configured.length,
    missing_count: missing.length,
    percentage_complete: Math.round((configured.length / requiredVars.length) * 100),
    status: missing.length === 0 ? 'ALL_CONFIGURED' : 'MISSING_VARS',
    missing_vars: missing,
    configured_vars: configured,
    detailed_results: results
  }
};

const envCheck = $('Check Environment Variables').item.json;
const airtableTest = $items('Test Airtable Connection');

const report = {
  timestamp: new Date().toISOString(),
  environment_variables: {
    status: envCheck.status,
    configured: `${envCheck.configured_count}/${envCheck.total_required}`,
    percentage: envCheck.percentage_complete + '%',
    missing: envCheck.missing_vars
  },
  airtable_connection: {
    status: airtableTest.length >= 0 ? 'SUCCESS' : 'FAILED',
    base_id: $env.AIRTABLE_BASE_ID,
    records_found: airtableTest.length
  },
  test_mode: {
    active: $env.TEST_MODE === 'true',
    value: $env.TEST_MODE
  },
  overall_status: envCheck.status === 'ALL_CONFIGURED' && airtableTest.length >= 0 ? 'READY' : 'NEEDS_SETUP'
};

console.log('=== PHASE 00 VERIFICATION REPORT ===');
console.log(JSON.stringify(report, null, 2));

return { json: report };
