const first = $input.first();
const root = first.json && first.json.body ? first.json.body : first.json;
function sanitizeEmail(v){ if(!v) return ''; const s=String(v).trim().toLowerCase(); return s.replace(/[\s]+/g,'').replace(/[\.,;:]+$/, ''); }
function normalizePhone(v){ if(!v) return ''; let s=String(v).trim(); if (s.startsWith('+')) s = '+'+s.slice(1).replace(/[^0-9]/g,''); else s=s.replace(/[^0-9]/g,''); if (s.startsWith('+')) return s; if (s.length===11 && s.startsWith('1')) return '+1'+s.slice(1); if (s.length===10) return '+1'+s; return s; }
function deriveDomain(email, company){ if(email){ const at=email.split('@')[1]; if(at) return at; } if(company){ return String(company).toLowerCase().replace(/[^a-z0-9-]/g,'') + '.com'; } return ''; }
const email = sanitizeEmail(root.email || root.Email || root.email_address || '');
const phone = normalizePhone(root.phone || root.Phone || root.phone_number || '');
const firstName = root.first_name || root.firstName || root.first || '';
const lastName = root.last_name || root.lastName || root.last || '';
const company = root.company || root.Company || '';
const title = root.title || root.job_title || '';
const domain = deriveDomain(email, company);
const out = {
  'Email': email,
  'Phone': phone,
  'First Name': firstName,
  'Last Name': lastName,
  'Company': company,
  'Title': title,
  'Company Domain': domain,
  'Source': 'Webhook',
  'Processing Status': 'Queued'
};
return [{ json: out }];
